def main():
    bot = ApplicationBuilder().token(TOKEN).build()

    commands = {
        "mute": mute,
        "unmute": unmute,
        "ban": ban,
        "unban": unban,
        "listwarn": listwarn,
        "warn": warn,
        "unwarn": unwarn,
        "resetwarn": resetwarn,
        "start": start,
        "rules": rules,
        "moodle": moodle,
        "links": links,
        "scores": scores,
        "plan": plan,
        "maptuke": maptuke,
        "map5p": map5p,
        "studijne": studijne,
        "schedule": schedule,
        "invite": invite,
        "week": week,
        "meme": meme,
        "predict": predict,
        "weather": weather,
        "chance": chance
    }

    for command, handler in commands.items():
        bot.add_handler(CommandHandler(command, handler))

    bot.add_handler(MessageHandler(filters.ChatType.PRIVATE & ~filters.COMMAND, start_message))
    bot.add_handler(MessageHandler(filters.StatusUpdate.NEW_CHAT_MEMBERS, welcome))
    bot.add_handler(MessageHandler((filters.ChatType.GROUP | filters.ChatType.SUPERGROUP) & ~filters.COMMAND, reaction))

    print("Starting Telegram Bot...")
    bot.run_polling(drop_pending_updates=True)


